mapper.select:
select id, eplId, eplName, triggerState, triggerTime, status, opinion, dealTime, fundid, orgid, orgname, textState, eplDescribe, thresholds, startTime, endTime from ( select tr.id, tr.epl_id eplId, tr.epl_name eplName, tr.trigger_state triggerState, tr.trigger_time triggerTime, substring(tr.end_time, 0, 19) endTime, substring(tr.start_time, 0, 19) startTime, tr.status, tr.opinion, tr.deal_time dealTime, tr.fundid, tj.orgid, tj.orgname, tt.text_state textState, tt.epl_describe eplDescribe, tt.thresholds from rtc_rule_trigger_record tr join ( select e.epl_id, e.text_state,e.epl_describe,concat(group_concat(concat(t.threshold_name, '=', t.threshold_value))) thresholds from rtc_epl e left join rtc_threshold t on (e.epl_id=t.epl_id)
and e.epl_id = #{eplId,jdbcType=INTEGER}
group by e.text_state,e.epl_id,e.epl_describe ) tt on(tr.epl_id=tt.epl_id) left join ( select tc.custid, tc.orgid, tg.orgname from customer tc, org tg where tc.orgid=tg.orgid
and tc.orgid = #{orgid,jdbcType=VARCHAR}
and tc.custid = #{fundid,jdbcType=BIGINT}
) tj on (tr.fundid=tj.custid) where 1=1
and tr.fundid = #{fundid,jdbcType=BIGINT}
and tc.orgid = #{orgid,jdbcType=VARCHAR}
and tr.status = #{status,jdbcType=INTEGER}
and tr.epl_id = #{eplId,jdbcType=INTEGER}
and tr.trigger_time >= str_to_date(#{startDate,jdbcType=DATE},'%Y-%m-%d %H:%i:%S')
and tr.trigger_time <= str_to_date(#{endDate,jdbcType=DATE},'%Y-%m-%d %H:%i:%S')
order by tr.trigger_time desc ) ttt

mapper.update:
UPDATE rtc_rule_trigger_record SET
	opinion = #{opinion},
	status = #{status},
	readFlag = #{readFlag}
WHERE id = #{id}

mapper.select:
select tt.epl_id eplId, tt.epl_name eplName, count(*) totalCount, sum(case when tt.status = 1 then 1 else 0 end) dealCount, sum(case when tt.status = 2 then 1 else 0 end) ignoreCount, sum(case when tt.status = 3 then 1 else 0 end) undealCount, sum(case when tt.status = 4 then 1 else 0 end) verificationCount from rtc_rule_trigger_record tt where 1=1
and tt.status = #{status}
and tt.epl_id = #{eplId}
and tt.trigger_time >= str_to_date(#{startDate,jdbcType=DATE},'%Y-%m-%d %H:%i:%S')
and tt.trigger_time <= str_to_date(#{endDate,jdbcType=DATE},'%Y-%m-%d %H:%i:%S')
group by tt.epl_id,tt.epl_name order by tt.epl_id desc

select count(*) totalCount, IFNULL(sum(case when status = 1 then 1 else 0 end), 0) dealCount, IFNULL(sum(case when status = 2 then 1 else 0 end), 0) ignoreCount, IFNULL(sum(case when status = 3 then 1 else 0 end), 0) undealCount, IFNULL(sum(case when status = 4 then 1 else 0 end), 0) verificationCount from rtc_rule_trigger_record where 1=1
and status = #{status}
and epl_id = #{eplId}
and trigger_time >= str_to_date(#{startDate,jdbcType=DATE},'%Y-%m-%d %H:%i:%S')
and trigger_time <= str_to_date(#{endDate,jdbcType=DATE},'%Y-%m-%d %H:%i:%S')
